function suma(e,n){var e=parseInt(document.getElementById("num1").value),n=parseInt(document.getElementById("num2").value);suma=e+n,document.getElementById("res").value=suma}function resta(e,n){var e=parseInt(document.getElementById("num1").value),n=parseInt(document.getElementById("num2").value);resta=e-n,document.getElementById("res").value=resta}function multiplicacion(e,n){var e=parseInt(document.getElementById("num1").value),n=parseInt(document.getElementById("num2").value);multiplicacion=e*n,document.getElementById("res").value=multiplicacion}function division(e,n){var e=parseInt(document.getElementById("num1").value),n=parseInt(document.getElementById("num2").value);division=e/n,document.getElementById("res").value=division}